INSERT INTO `products` VALUES (1289,'2248','ΕΛΑΙΟΡΑΒΔΙΣΤΙΚΟ ZANON AL 300K KARBONIUM EVO  12V','<table class=\"mceItemTable\" style=\"width: 485px;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\"><colgroup><col width=\"485\" /> </colgroup>\r\n<tbody>\r\n<tr>\r\n<td width=\"485\" height=\"20\">ΖΑΝΟΝ AL 300K KARBONIUM EVO ΕΛΑΙΟΡΑΣΔΙΣΤΙΚΟ 12V DC</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"39\">Το KARBONIUM είναι προϊόν υψηλής τεχνολογίας και δίνει απάντηση στην εύκολη και γρήγορη συγκομιδή της ελιάς.</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"58\">Μπορεί να χρησιμοποιηθεί σε όλα τα είδη της ελιάς και σε όλα τα στάδια ωριμότητας εξασφαλίζοντας υψηλές αποδόσεις στον χρήστη, που κυμαίνονται από 80 – 150 kg/ώρα.</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"77\">Ο εργονομικός του σχεδιασμός επιτρέπει την διεισδυτικότητα του πολύ εύκολα μέσα στα κλαδιά, όπως επίσης και τη δυνατότητα ρύθμισης του ύψους εργασίας από 2,1 μέτρα έως 3,4 μέτρα μέσω της τηλεσκοπικής προέκτασης.</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"96\">Η κεφαλή KARBONIUM τροφοδοτείται από ένα πανίσχυρο μοτέρ 500 Watt δίνοντας κίνηση στα 8 αποσπώμενα ανθρακονήματα μέσω ανοξείδωτων γραναζιών. Έτσι εξασφαλίζεται η παλινδρομική κίνηση των χτενιών (ανθρακονημάτων) που έχουν μήκος 30cm και επιφάνεια εργασίας από 25 έως 50 cm.</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"77\">Κύρια πλεονεκτήματα του η υψηλή απόδοση, η αθόρυβη λειτουργία, η ανυπαρξία δονήσεων, το χαμηλό βάρος “2,4kg”, η χαμηλή και ακίνδυνη κατανάλωση ρεύματος “3 – 4 A” προσφέροντας έτσι μεγάλη αυτονομία, εύκολη συντήρηση και αποθήκευση.</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"39\">* Οι αποδόσεις είναι σε συνάρτηση με το είδος της ελιάς και την ταχύτητα εργασίας του χρήστη.</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"20\">Τεχνικά Χαρακτηριστικά</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"20\">Ισχύς (Watt)500</td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"20\">Κατανάλωση (A)3 – 4</td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"20\">Καλώδιο 15m</td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"20\">Ύψος (m)2,1 – 3,4</td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"20\">Βάρος (Kg)2,4</td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"20\">Απόδοση 80 – 150 kg/ώρα.</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"39\">Συνοδεύεται με μετατροπέα - σταθεροποιητή ZDC (Είσοδος 12v - \'Εξοδος 34v DC)</td>\r\n</tr>\r\n<tr>\r\n<td height=\"20\"> </td>\r\n</tr>\r\n<tr>\r\n<td width=\"485\" height=\"20\"><span style=\"color: #ff0000;\">Δεν περιλαμβάνει μπαταρία - Η μπαταρία πωλείται ξεχωριστά</span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><iframe src=\"//www.youtube.com/embed/qiKg966egtk\" width=\"420\" height=\"315\" frameborder=\"0\" allowfullscreen=\"allowfullscreen\"></iframe></p>',796.75,10,1,NULL,0,0,0,0,0,'2021-09-18 05:00:00'),(1291,'2250','ΜΗΧΑΝΗ ΓΚΑΖΟΝ ΗΛΕΚΤΡΙΚΗ WOLF GARTEN AMBITION 32E','<p style=\"text-align: left;\">Ηλεκτρική ωθούμενη μηχανή γκαζόν Wolf Garten Ambition 32e, ισχύος 1200W.</p>\r\n<p style=\"text-align: left;\">Το πλάτος κοπής της είναι 32cm και το βάρος της είναι 10kg.</p>\r\n<p style=\"text-align: left;\">Ενδείκνυται για γκαζόν έκτασης έως 250m.</p>\r\n<p style=\"text-align: left;\">Διαστάσεις κυτίου 61,5x38,4x32,2cm</p>\r\n<p style=\"text-align: left;\"> </p>',65.04,10,1,NULL,38.4,32.2,61.5,10,0,'2021-09-1